
public class chap04_04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		switch(a) {
		case 5:
			System.out.println("숫자는 5입니다.");
		break;
		case 6:
			System.out.println("숫자는 5가 아닙니다.");
		break;
		default:
			System.out.println("숫자가 아닙니다.");
		}

	}

}
